import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import FileBase from 'react-file-base64';

import useStyles from './styles';
import { createPost, updatePost } from '../../actions/posts';

const Form = ({ currentId, setCurrentId }) => {
  const [postData, setPostData] = useState({ Name: '', Party: '', Office: '', Incumbent: '', selectedFile: '' });
  const post = useSelector((state) => (currentId ? state.posts.find((Office) => Office._id === currentId) : null));
  const dispatch = useDispatch();
  const classes = useStyles();

  useEffect(() => {
    if (post) setPostData(post);
  }, [post]);

  const clear = () => {
    setCurrentId(0);
    setPostData({ Name: '', Party: '', Office: '', Incumbent: '', selectedFile: '' });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (currentId === 0) {
      dispatch(createPost(postData));
      clear();
    } else {
      dispatch(updatePost(currentId, postData));
      clear();
    }
  };

  return (
    <Paper className={classes.paper}>
      <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
        <Typography variant="h6">{currentId ? `Editing "${post.title}"` : 'Creating or Editing a candiate'}</Typography>
        <TextField name="creator" variant="outlined" label="Name" fullWidth value={postData.Name} onChange={(e) => setPostData({ ...postData, Name: e.target.value })} />
        <TextField name="title" variant="outlined" label="Party" fullWidth value={postData.Party} onChange={(e) => setPostData({ ...postData, Party: e.target.value })} />
        <TextField name="message" variant="outlined" label="Office" fullWidth multiline rows={4} value={postData.Office} onChange={(e) => setPostData({ ...postData, Office: e.target.value })} />
        <TextField name="tags" variant="outlined" label="Tags (coma separated, e.g. )" fullWidth value={postData.Incumbent} onChange={(e) => setPostData({ ...postData, Incumbent: e.target.value.split(',') })} />
        <div className={classes.fileInput}><FileBase type="file" multiple={false} onDone={({ base64 }) => setPostData({ ...postData, selectedFile: base64 })} /></div>
        <Button className={classes.buttonSubmit} variant="contained" color="primary" size="large" type="submit" fullWidth>Submit</Button>
        <Button variant="contained" color="secondary" size="small" onClick={clear} fullWidth>Clear</Button>
      </form>
    </Paper>
  );
};

export default Form;
